mapper.select:
SELECT * FROM MEMBER
WHERE m_id = #{m_id}
AND m_type = 'M'

		SELECT COUNT(*) FROM MEMBER
		WHERE NOT m_type = 'M'

	    	AND ${searchFiled} like '%'||#{searchValue}||'%'


			AND ${m_statusFiled} = #{m_statusValue}


     		AND ${m_statusFiled} = #{m_statusValue}


			AND ${m_typeFiled} = #{m_typeValue}


     		AND ${m_typeFiled} = #{m_typeValue}

	    SELECT A.*
        FROM (SELECT ROWNUM AS RNUM1, AA.*
            FROM (SELECT * FROM MEMBER
            	  WHERE NOT m_type = 'M'

			     	AND ${searchFiled} like '%'||#{searchValue}||'%'


			     	AND ${m_statusFiled} = #{m_statusValue}


			     	AND ${m_statusFiled} = #{m_statusValue}


					AND ${m_typeFiled} = #{m_typeValue}


     				AND ${m_typeFiled} = #{m_typeValue}

		         ORDER BY m_name DESC
                  ) AA ) A

        WHERE RNUM1 >= #{startRowNo}
        AND RNUM1 <= #{endRowNo}

mapper.update:
UPDATE MEMBER
SET m_status = 'Y'
WHERE m_id = #{m_id}

UPDATE MEMBER
SET m_status = 'N'
WHERE m_id = #{m_id}

mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE m_id = #{m_id}
AND m_password = #{m_password}
AND m_type = 'M'

mapper.update:
UPDATE FAQ
SET f_sub = #{f_sub}, f_content = #{f_content}
WHERE f_num = #{f_num}

mapper.select:
	    SELECT x.*
        FROM (SELECT ROWNUM AS RNUM1, AA.*
            FROM (SELECT accept.*,board.b_subject FROM accept 
            inner join board on accept.b_num = board.b_num
             where a_apply='Y'


			             and ${searchFiled} like '%'||#{searchValue}||'%'


			          		 order by b_subject desc


                  ) AA ) x

        WHERE RNUM1 >= #{startRowNo}
        AND RNUM1 <= #{endRowNo}

	    SELECT A.*
        FROM (SELECT ROWNUM AS RNUM1, AA.*
            FROM (SELECT danger.*,board.b_subject FROM danger
                  inner join board on danger.b_num = board.b_num


			              WHERE ${searchFiled} like '%'||#{searchValue}||'%'



                  ) AA ) A

        WHERE RNUM1 >= #{startRowNo}
        AND RNUM1 <= #{endRowNo}
